import React, { useRef, useState, useEffect } from 'react';
import WAVES from "vanta/dist/vanta.waves.min";
import * as THREE from 'three';
import { Link, animateScroll as scroll } from "react-scroll";

window.THREE = THREE;

function App() {
  const myRef = useRef(null);
  const [vantaEffect, setVantaEffect] = useState(0);

  useEffect(() => {
    if (!vantaRef.current) {
      vantaRef.current = WAVES({
        el: myRef.current,
        THREE: THREE,
        color: 0x1a74db, // Nice blue color
        shininess: 50,
        waveHeight: 20,
        waveSpeed: 0.5,
        zoom: 0.9
      });
    }
    return () => {
      if (vantaRef.current) {
        vantaRef.current.destroy();
        vantaRef.current = 0;
      }
    };
  }, []);

  return (
    <div className="App" ref={myRef}>
      <Navbar />
      <Home />
      <OurTeam />
      <OurWork />
      <ContactUs />
    </div>
  );
}

function Navbar() {
  return (
    <div>
      <Link 
        to="homeSection" 
        smooth={true} 
        duration={500}
      >
        Home
      </Link>
      <Link 
        to="teamSection" 
        smooth={true} 
        duration={500}
      >
        Our Team
      </Link>
      <Link 
        to="workSection" 
        smooth={true} 
        duration={500}
      >
        Our Work
      </Link>
      <Link 
        to="contactSection" 
        smooth={true} 
        duration={500}
      >
        Contact Us
      </Link>
    </div>
  );
}

function Home() {
  return (
    <div id="homeSection">
      {/* Home content */}
    </div>
  );
}

function OurTeam() {
  return (
    <div id="teamSection">
      {/* OurTeam content */}
    </div>
  );
}

function OurWork() {
  return (
    <div id="workSection">
      {/* OurWork content */}
    </div>
  );
} 

function ContactUs() {
  return (
    <div id="contactSection">
      {/* ContactUs content */}
    </div>
  );
}

export default App;
